var Client = require('svn-spawn')

var client = new Client()

var getAllByPath = function(path, callback){
	client.cmd(['list', '-R', path], function(err, data){
		data = data.trim().split('\n')
		callback(err, data)
	})
}

var getSubByPath = function(path, callback){
	client.cmd(['list', path], function(err, data){
		data = data.trim().split('\n')
		callback(err, data)
	})
}

exports.getAllByPath = getAllByPath
exports.getSubByPath = getSubByPath
